import axios from "axios";

export default class Api {

    constructor(baseUrl){
        this.baseUrl = baseUrl;
    }
    async get(url){
        return (await axios.get(`${this.baseUrl}${url}`)).data
    }
    async getQuotes(){
        return await this.get("/quotes?_expand=author");
    }

}

export const api = new Api("http://localhost:3000");
