import React, { Component } from "react";
import withRouter from "../lib/withRouter";
import {api} from "../lib/Api";
import { Container } from "@mui/system";
import { Card, CardContent, Grid, Typography } from "@mui/material";


class Author extends React.Component {
    // state = {
    //     "authors":[]
    // };
    state = {author:{id:"", name:"", quotes:[]}}; 
        
    render(){
        return (
            <div>
                {this.props.params.id}
                {this.state.author.name}




                <Container>
                    <Grid container spacing={2}>
                        {
                            this.state.author.quotes.map(quote=>{
                                return(
                                    <Grid item xs={4} key={quote.id}> 
                                        <Card>
                                            <CardContent>
                                                <Typography variant="body2">{quote.content}</Typography>
                                            </CardContent>
                                        </Card>
                                    </Grid>
                                )
                            })        
                        }
                    </Grid>
                {
                    this.state.author.map((v)=>{
                        return(
                            <dr>
                                <td>{v.id}</td> 
                                <td>{v.name}</td>
                                <br/>
                            </dr>
                        );
                    })
                }
                </Container>

            </div>


        );
    }

    async componentDidMount(){
        api.getAuthor(this.props.params.id).then(author =>{
            this.setState({author});
        });
        
        // const authors = await api.get();
        // this.setState({author:});
    }
}
export default withRouter(Author);
