import Button from "@mui/material/Button";
import { Card, CardContent, Typography } from "@mui/material";
import { Box, Container } from "@mui/system";

import {api} from "../lib/Api";
import SearchField from "./SearchField";

export default class QuotesNew extends React.Component {
    state = {
        "authors":[],
        "quote": '',
        "authorId": ''
    };
    
    
    render(){
        return ( 
            <Container maxWidth = "sm">

                <label>
                Author:
                    <select name="authorId" value={this.state.authorId} onChange={this.handleChange}>
                        <option value="">Wesh donne 1 ot'heur</option>
                        {
                            this.state.authors.map(author => (
                                <option key={author.id} value={author.id}>{author.name}</option>
                            ))
                        }
                    </select>
                </label>
                <br/>
         
                <label>
                Quote:
                    <textarea id="content" name="content"/><br/>
                </label>            

                <Button>Valider</Button>
            </Container>
        )
    }
    
    
    handleChange = (event) => {
        this.setState({ [event.target.name]: event.target.value });
