import React, { Component } from "react";
import axios from "axios";

import Button from "@mui/material/Button";
import { Card, CardContent } from "@mui/material";
import { Box, Container } from "@mui/system";


export default class QuotesList extends React.Component {
    state = {
        "quotes":[]
    };
    // render(){
    //     return <div>
    //         <Button>Valider</Button>
    //         <Card>
    //             <CardContent>
    //                 { quote }
    //             </CardContent>
    //         </Card>

    //         <ul>
    //             {
    //                 this.state.quotes.map(item => <li>Perso : {item.actor} Citation : {item.content}</li>)
    //             }
    //         </ul>
    //     </div> 
        
    render(){
        return ( 
            <Container maxWidth = "sm">
                <Box md={3}>
                    <Button>Valider</Button>
                    <ul>
                        {
                            this.state.quotes.map((v)=>{
                                return(
                                    <Card>
                                        <CardContent>
                                            {v.actor} : {v.content}
                                        </CardContent>
                                    </Card>
                                )
                            })
                            //this.state.quotes.map(v=><Card><li>{v.content}</li></Card>)
                        }
                    </ul>

                </Box>
            </Container>
        )
    }

    componentDidMount(){
        axios.get('http://localhost:3000/quotes')
        .then(response => this.setState({"quotes" : response.data}));
    }

}
